// JavaScript Functionality
const javascriptCode = `
// DOM Content Loaded Event
document.addEventListener('DOMContentLoaded', function() {
    console.log('Web Development Guide loaded!');

    // Smooth scrolling for navigation links
    const navLinks = document.querySelectorAll('nav a[href^="#"]');

    navLinks.forEach(link => {
        link.addEventListener('click', function(e) {
            e.preventDefault();

            const targetId = this.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);

            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });

    // Add interactive features
    const sections = document.querySelectorAll('section');

    sections.forEach(section => {
        section.addEventListener('mouseenter', function() {
            this.style.transform = 'translateY(-5px)';
            this.style.transition = 'transform 0.3s ease';
        });

        section.addEventListener('mouseleave', function() {
            this.style.transform = 'translateY(0)';
        });
    });

    // Dynamic content loading
    loadDynamicContent();
});

// Function to load dynamic content
function loadDynamicContent() {
    const sections = document.querySelectorAll('section');

    sections.forEach(section => {
        const h2 = section.querySelector('h2');
        if (h2) {
            const sectionId = section.getAttribute('id');
            const content = getSectionContent(sectionId);

            if (content) {
                section.innerHTML = `
                    <h2>${h2.textContent}</h2>
                    <p>${content.description}</p>
                    <div class="content-details">
                        ${content.details.map(detail => `<p>â€¢ ${detail}</p>`).join('')}
                    </div>
                `;
            }
        }
    });
}

// Content data for different sections
function getSectionContent(sectionId) {
    const contentData = {
        'html': {
            description: 'HTML (HyperText Markup Language) is the standard markup language for creating web pages.',
            details: [
                'Semantic HTML5 elements for better structure',
                'Accessibility features with ARIA attributes',
                'Form elements and validation',
                'Media elements for images and videos'
            ]
        },
        'css': {
            description: 'CSS (Cascading Style Sheets) is used to style and layout web pages.',
            details: [
                'Flexbox and Grid for modern layouts',
                'Responsive design with media queries',
                'CSS animations and transitions',
                'CSS custom properties (variables)'
            ]
        },
        'js': {
            description: 'JavaScript is a programming language that adds interactivity to web pages.',
            details: [
                'ES6+ features and modern syntax',
                'DOM manipulation and event handling',
                'Asynchronous programming with Promises',
                'Module system and bundling'
            ]
        }
    };

    return contentData[sectionId] || null;
}

// Export for module systems
if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        htmlTemplate,
        cssStyles,
        javascriptCode
    };
}
`;

